mapper.select:
SELECT n.n_number, n_title, n_content, n_url, n_date, u.user_nickname
FROM notice n, user_info u
WHERE n.user_number = u.user_number AND n_number = #{no}

mapper.update:
UPDATE notice
SET n_title = #{title}, n_content = #{content}, date = SYSDATE, n_url = #{url}
WHERE n_number = #{no}
